@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400;500;700;900&family=Inter:wght@300;400;500;600;700&family=JetBrains+Mono:wght@400;500;600&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(230, 25%, 6%);
  --foreground: hsl(210, 40%, 98%);
  --card: hsl(230, 25%, 8%);
  --card-foreground: hsl(210, 40%, 95%);
  --popover: hsl(230, 25%, 8%);
  --popover-foreground: hsl(210, 40%, 95%);
  --primary: hsl(210, 100%, 50%);
  --primary-foreground: hsl(0, 0%, 100%);
