  --chart-2: hsl(159.7826, 100%, 36.0784%);
  --chart-3: hsl(42.0290, 92.8251%, 56.2745%);
  --chart-4: hsl(147.1429, 78.5047%, 41.9608%);
  --chart-5: hsl(341.4894, 75.2000%, 50.9804%);
  --sidebar: hsl(230, 25%, 8%);
  --sidebar-foreground: hsl(210, 40%, 95%);
  --sidebar-primary: hsl(210, 100%, 50%);
  --sidebar-primary-foreground: hsl(0, 0%, 100%);
  --sidebar-accent: hsl(210, 100%, 75%);
  --sidebar-accent-foreground: hsl(230, 25%, 6%);
  --sidebar-border: hsl(230, 25%, 12%);
  --sidebar-ring: hsl(210, 100%, 50%);
  --font-sans: Inter, system-ui, sans-serif;
