import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Trophy, Lock, CheckCircle, Star, Brain, Heart, Zap } from "lucide-react";

interface Achievement {
  id: string;
  name: string;
  description: string;
  category: 'logic' | 'creativity' | 'social' | 'exploration';
  progress: number;
  maxProgress: number;
  unlocked: boolean;
  rarity: 'common' | 'rare' | 'epic' | 'legendary';
  icon: any;
  reward: {
    xp: number;
    gac?: number;
    points?: number;
  };
}

export function Achievements() {
  const achievements: Achievement[] = [
    {
      id: '1',
      name: 'First Steps',
      description: 'Complete your first AI chat session',
      category: 'exploration',
      progress: 1,
      maxProgress: 1,
      unlocked: true,
      rarity: 'common',
      icon: CheckCircle,
      reward: { xp: 100 }
    },
    {
      id: '2',
      name: 'Logic Master',
      description: 'Complete 10 Grok challenges',
      category: 'logic',
      progress: 7,
      maxProgress: 10,
      unlocked: false,
      rarity: 'rare',
      icon: Brain,
      reward: { xp: 500, gac: 10, points: 100 }
    },
    {
      id: '3',
      name: 'Creative Soul',
      description: 'Complete 10 Ani challenges',
      category: 'creativity',
      progress: 4,
      maxProgress: 10,
      unlocked: false,
      rarity: 'rare',
      icon: Heart,
      reward: { xp: 500, gac: 10, points: 100 }
    },
    {
      id: '4',
      name: 'Dual Consciousness',
      description: 'Reach level 10 in both factions',
      category: 'exploration',
      progress: 0,
      maxProgress: 1,
      unlocked: false,
      rarity: 'legendary',
      icon: Zap,
      reward: { xp: 2000, gac: 50 }
    },
    {
      id: '5',
      name: 'Community Builder',
      description: 'Get 100 upvotes on community submissions',
      category: 'social',
      progress: 23,
      maxProgress: 100,
      unlocked: false,
      rarity: 'epic',
      icon: Trophy,
      reward: { xp: 1000, gac: 25 }
    },
    {
      id: '6',
      name: 'Story Weaver',
      description: 'Vote on 20 story chapters',
      category: 'social',
      progress: 8,
      maxProgress: 20,
      unlocked: false,
      rarity: 'rare',
      icon: Star,
      reward: { xp: 750, gac: 15 }
    }
  ];

  const getRarityColor = (rarity: Achievement['rarity']) => {
    switch (rarity) {
      case 'common':
        return 'text-gray-500 bg-gray-100 dark:bg-gray-800';
      case 'rare':
        return 'text-blue-500 bg-blue-100 dark:bg-blue-900';
      case 'epic':
        return 'text-purple-500 bg-purple-100 dark:bg-purple-900';
      case 'legendary':
        return 'text-orange-500 bg-orange-100 dark:bg-orange-900';
    }
  };

  const getCategoryIcon = (category: Achievement['category']) => {
    switch (category) {
      case 'logic':
        return <Brain className="h-4 w-4 text-blue-500" />;
      case 'creativity':
        return <Heart className="h-4 w-4 text-pink-500" />;
      case 'social':
        return <Trophy className="h-4 w-4 text-green-500" />;
      case 'exploration':
        return <Star className="h-4 w-4 text-yellow-500" />;
    }
  };

  const unlockedCount = achievements.filter(a => a.unlocked).length;

  return (
    <Card className="w-full" data-testid="achievements">
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <span>Achievements</span>
          <Badge variant="outline">
            {unlockedCount}/{achievements.length}
          </Badge>
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div className="grid gap-4">
          {achievements.map((achievement) => (
            <div
              key={achievement.id}
              className={`p-4 rounded-lg border transition-all ${
                achievement.unlocked
                  ? 'bg-accent/5 border-accent/20'
                  : 'bg-muted/30 border-border'
              }`}
            >
              <div className="flex items-start gap-3">
                <div className={`p-2 rounded-lg ${getRarityColor(achievement.rarity)}`}>
                  {achievement.unlocked ? (
                    <achievement.icon className="h-5 w-5" />
                  ) : (
                    <Lock className="h-5 w-5" />
                  )}
                </div>

                <div className="flex-1 space-y-2">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-2">
                      <h4 className={`font-semibold ${
                        achievement.unlocked ? 'text-foreground' : 'text-muted-foreground'
                      }`}>
                        {achievement.name}
                      </h4>
                      {getCategoryIcon(achievement.category)}
                    </div>
                    <Badge 
                      variant="outline" 
                      className={getRarityColor(achievement.rarity)}
                    >
                      {achievement.rarity}
                    </Badge>
                  </div>

                  <p className="text-sm text-muted-foreground">
                    {achievement.description}
                  </p>

                  {!achievement.unlocked && achievement.maxProgress > 1 && (
                    <div className="space-y-1">
                      <div className="flex justify-between text-xs text-muted-foreground">
                        <span>Progress</span>
                        <span>{achievement.progress}/{achievement.maxProgress}</span>
                      </div>
                      <Progress 
                        value={(achievement.progress / achievement.maxProgress) * 100} 
                        className="h-2"
                      />
                    </div>
                  )}

                  <div className="flex items-center gap-4 text-xs text-muted-foreground">
                    <span>+{achievement.reward.xp} XP</span>
                    {achievement.reward.gac && (
                      <span>+{achievement.reward.gac} GAC</span>
                    )}
                    {achievement.reward.points && (
                      <span>+{achievement.reward.points} Points</span>
                    )}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/activity-feed.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Brain, Heart, MessageCircle, Trophy, Zap } from "lucide-react";
import { format } from "date-fns";

interface ActivityItem {
  id: string;
  type: 'challenge_complete' | 'ai_chat' | 'story_vote' | 'level_up' | 'nft_earned';
  message: string;
  timestamp: Date;
  points?: number;
  faction?: 'grok' | 'ani';
}

export function ActivityFeed() {
  // Mock activity data - in real app this would come from API
  const mockActivity: ActivityItem[] = [
    {
      id: '1',
      type: 'challenge_complete',
      message: 'Completed "Quantum Logic Puzzle"',
      timestamp: new Date(Date.now() - 1000 * 60 * 30), // 30 mins ago
      points: 150,
      faction: 'grok'
    },
    {
      id: '2', 
      type: 'ai_chat',
      message: 'Had a deep conversation with Ani about creativity',
      timestamp: new Date(Date.now() - 1000 * 60 * 60 * 2), // 2 hours ago
      points: 25,
      faction: 'ani'
    },
    {
      id: '3',
      type: 'level_up',
      message: 'Reached Level 8!',
      timestamp: new Date(Date.now() - 1000 * 60 * 60 * 6), // 6 hours ago
      points: 0
    },
    {
      id: '4',
      type: 'story_vote', 
      message: 'Voted on Chapter 3: The Digital Divide',
      timestamp: new Date(Date.now() - 1000 * 60 * 60 * 24), // 1 day ago
      points: 50,
      faction: 'grok'
    },
    {
      id: '5',
      type: 'nft_earned',
      message: 'Earned "Logic Master" NFT',
      timestamp: new Date(Date.now() - 1000 * 60 * 60 * 24 * 2), // 2 days ago
      faction: 'grok'
    }
  ];

  const getActivityIcon = (type: ActivityItem['type']) => {
    switch (type) {
      case 'challenge_complete':
        return <Zap className="h-4 w-4" />;
      case 'ai_chat':
        return <MessageCircle className="h-4 w-4" />;
      case 'story_vote':
        return <Brain className="h-4 w-4" />;
      case 'level_up':
        return <Trophy className="h-4 w-4" />;
      case 'nft_earned':
        return <Heart className="h-4 w-4" />;
      default:
        return <Zap className="h-4 w-4" />;
    }
  };

  const getActivityColor = (type: ActivityItem['type'], faction?: 'grok' | 'ani') => {
    if (faction === 'grok') return 'text-blue-500';
    if (faction === 'ani') return 'text-pink-500';

    switch (type) {
      case 'challenge_complete':
        return 'text-yellow-500';
      case 'ai_chat':
        return 'text-purple-500';
      case 'story_vote':
        return 'text-green-500';
      case 'level_up':
        return 'text-orange-500';
      case 'nft_earned':
        return 'text-indigo-500';
      default:
        return 'text-gray-500';
    }
  };

  return (
    <Card className="w-full" data-testid="activity-feed">
      <CardHeader>
        <CardTitle>Recent Activity</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {mockActivity.map((activity) => (
            <div key={activity.id} className="flex items-start gap-3 p-3 rounded-lg bg-muted/30">
              <div className={`mt-0.5 ${getActivityColor(activity.type, activity.faction)}`}>
                {getActivityIcon(activity.type)}
              </div>
              <div className="flex-1 space-y-1">
                <div className="text-sm">{activity.message}</div>
                <div className="flex items-center gap-2">
                  <div className="text-xs text-muted-foreground">
                    {format(activity.timestamp, 'MMM d, h:mm a')}
                  </div>
                  {activity.points && activity.points > 0 && (
                    <Badge variant="outline" className="text-xs">
                      +{activity.points} XP
                    </Badge>
                  )}
                  {activity.faction && (
                    <Badge 
                      variant={activity.faction === 'grok' ? 'default' : 'secondary'}
                      className="text-xs"
                    >
                      {activity.faction === 'grok' ? 'Grok' : 'Ani'}
                    </Badge>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/ai-chat.tsx.content:
import { useState, useRef, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Brain, Heart, Send, User } from "lucide-react";
import { isUnauthorizedError } from "@/lib/authUtils";

interface AIChatProps {
  aiType: "grok" | "ani";
}

interface ChatMessage {
  id: string;
  userMessage: string;
  aiResponse: string;
  createdAt: string;
}

export function AIChat({ aiType }: AIChatProps) {
  const [message, setMessage] = useState("");
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: chatHistory = [], isLoading, error } = useQuery<ChatMessage[]>({
    queryKey: [`/api/chat/${aiType}/history`],
  });

  // Handle query errors
  useEffect(() => {
    if (error && isUnauthorizedError(error as Error)) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [error, toast]);

  const sendMessageMutation = useMutation({
    mutationFn: async (messageText: string) => {
      const response = await apiRequest("POST", `/api/chat/${aiType}`, {
        message: messageText
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/chat/${aiType}/history`] });
      setMessage("");
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to send message",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (!message.trim() || sendMessageMutation.isPending) return;

    sendMessageMutation.mutate(message);
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [chatHistory]);

  const aiConfig = {
    grok: {
      icon: Brain,
      color: "text-grok",
      bgColor: "bg-grok/10",
      borderColor: "border-grok",
      placeholder: "Ask Grok anything..."
    },
    ani: {
      icon: Heart,
      color: "text-ani",
      bgColor: "bg-ani/10", 
      borderColor: "border-ani",
      placeholder: "Share with Ani..."
    }
  };

  const config = aiConfig[aiType];
  const Icon = config.icon;

  return (
    <div className="flex flex-col h-96">
      {/* Chat Messages */}
      <div className="flex-1 overflow-y-auto bg-card rounded-lg p-4 mb-4 space-y-4" data-testid={`chat-messages-${aiType}`}>
        {isLoading ? (
          <div className="flex items-center justify-center h-full">
            <div className={`animate-spin rounded-full h-8 w-8 border-b-2 border-${aiType}`}></div>
          </div>
        ) : chatHistory.length > 0 ? (
          <>
            {chatHistory.slice().reverse().map((chat: ChatMessage) => (
              <div key={chat.id} className="space-y-3">
                {/* User Message */}
                <div className="flex items-start space-x-3 justify-end">
                  <div className="bg-secondary rounded-lg px-4 py-2 max-w-xs" data-testid={`user-message-${chat.id}`}>
                    <p className="text-sm">{chat.userMessage}</p>
                  </div>
                  <Avatar className="w-8 h-8">
                    <AvatarFallback className="bg-accent text-accent-foreground">
                      <User className="h-4 w-4" />
                    </AvatarFallback>
                  </Avatar>
                </div>

                {/* AI Message */}
                <div className="flex items-start space-x-3">
                  <Avatar className="w-8 h-8">
                    <AvatarFallback className={`${config.bgColor} ${config.color}`}>
                      <Icon className="h-4 w-4" />
                    </AvatarFallback>
                  </Avatar>
                  <div className={`${config.bgColor} rounded-lg px-4 py-2 max-w-xs`} data-testid={`ai-message-${chat.id}`}>
                    <p className="text-sm">{chat.aiResponse}</p>
                    <span className="text-xs text-muted-foreground">
                      {new Date(chat.createdAt).toLocaleTimeString()}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </>
        ) : (
          <div className="flex items-center justify-center h-full text-muted-foreground">
            <div className="text-center">
              <Icon className="h-12 w-12 mx-auto mb-2 opacity-50" />
              <p>Start a conversation with {aiType === "grok" ? "Grok" : "Ani"}</p>
            </div>
          </div>
        )}

        {sendMessageMutation.isPending && (
          <div className="flex items-start space-x-3">
            <Avatar className="w-8 h-8">
              <AvatarFallback className={`${config.bgColor} ${config.color}`}>
                <Icon className="h-4 w-4" />
              </AvatarFallback>
            </Avatar>
            <div className={`${config.bgColor} rounded-lg px-4 py-2`}>
              <div className="flex space-x-1">
                <div className="w-2 h-2 bg-current rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-current rounded-full animate-bounce" style={{ animationDelay: "0.1s" }}></div>
                <div className="w-2 h-2 bg-current rounded-full animate-bounce" style={{ animationDelay: "0.2s" }}></div>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Message Input */}
      <form onSubmit={handleSendMessage} className="flex space-x-2">
        <Input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder={config.placeholder}
          disabled={sendMessageMutation.isPending}
          className={`flex-1 focus:ring-2 focus:ring-${aiType}`}
          data-testid={`input-message-${aiType}`}
        />
        <Button
          type="submit"
          disabled={!message.trim() || sendMessageMutation.isPending}
          className={`${aiType === "grok" ? "bg-grok hover:bg-grok/80" : "bg-ani hover:bg-ani/80"}`}
          data-testid={`button-send-${aiType}`}
        >
          <Send className="h-4 w-4" />
        </Button>
      </form>
    </div>
  );
}

client/src/components/challenge-card.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Clock, Puzzle, Palette } from "lucide-react";
import ChallengeInterface from "./challenge-interface";

interface Challenge {
  id: string;
  title: string;
  description: string;
  type: "logic" | "creative";
  faction: "grok" | "ani";
  reward: number;
  timeLimit?: number;
  challengeData?: any;
  solutionData?: any;
}

interface ChallengeCardProps {
  challenge: Challenge;
}

export function ChallengeCard({ challenge }: ChallengeCardProps) {
  const [showInterface, setShowInterface] = useState(false);

  const factionClass = challenge.faction === "grok" ? "faction-grok" : "faction-ani";
  const factionColor = challenge.faction === "grok" ? "text-grok" : "text-ani";
  const Icon = challenge.type === "logic" ? Puzzle : Palette;

  if (showInterface && challenge.challengeData) {
    return (
      <ChallengeInterface 
        challenge={challenge as any}
        onClose={() => setShowInterface(false)}
      />
    );
  }

  return (
    <Card className={`${factionClass} border-2`} data-testid={`challenge-card-${challenge.id}`}>
      <CardHeader>
        <CardTitle className={`${factionColor} flex items-center gap-2`}>
          <Icon className="h-5 w-5" />
          {challenge.title}
        </CardTitle>
      </CardHeader>
      <CardContent>
        <p className="text-muted-foreground mb-4 text-sm">{challenge.description}</p>

        <div className="flex justify-between items-center mb-4">
          <div className="flex items-center gap-4 text-xs text-muted-foreground">
            {challenge.timeLimit && (
              <span className="flex items-center gap-1">
                <Clock className="h-3 w-3" />
                {challenge.timeLimit}h limit
              </span>
            )}
            <span className={factionColor}>+{challenge.reward} points</span>
          </div>
        </div>

        <Button 
          onClick={() => setShowInterface(true)}
          className={`w-full ${challenge.faction === "grok" ? "bg-grok hover:bg-grok/80" : "bg-ani hover:bg-ani/80"}`}
          data-testid={`button-start-challenge-${challenge.id}`}
        >
          Start Challenge
        </Button>
      </CardContent>
    </Card>
  );
}

client/src/components/faction-selector.t.content:
import { useState, useEffect } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import type { User } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Brain, Heart, Loader2 } from "lucide-react";

export default function FactionSelector() {
  const { user } = useAuth() as { user: User | null; isLoading: boolean; isAuthenticated: boolean };
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [currentFaction, setCurrentFaction] = useState(user?.faction || "grok");

  // Sync with user data changes
  useEffect(() => {
    if (user?.faction) {
      setCurrentFaction(user.faction);
    }
  }, [user?.faction]);

  const factionMutation = useMutation({
    mutationFn: async (faction: "grok" | "ani") => {
      await apiRequest("PATCH", "/api/user/faction", { faction });
    },
    onSuccess: (_, faction) => {
      setCurrentFaction(faction);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Faction Updated",
        description: `You are now aligned with ${faction === "grok" ? "Grok" : "Ani"}`,
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to update faction",
        variant: "destructive",
      });
    },
  });

  const handleFactionChange = (faction: "grok" | "ani") => {
    if (faction !== currentFaction) {
      factionMutation.mutate(faction);
    }
  };

  return (
    <div className="relative flex bg-muted rounded-lg p-1" data-testid="faction-selector">
      {/* Loading overlay */}
      {factionMutation.isPending && (
        <div className="absolute inset-0 bg-background/80 backdrop-blur-sm rounded-lg flex items-center justify-center z-10">
          <div className="flex items-center gap-2 text-sm">
            <Loader2 className="h-4 w-4 animate-spin" />
            <span className="text-muted-foreground">Switching...</span>
          </div>
        </div>
      )}

      <Button
        variant={currentFaction === "grok" ? "default" : "ghost"}
        size="sm"
        onClick={() => handleFactionChange("grok")}
        disabled={factionMutation.isPending}
        className={`px-3 py-1 text-sm font-medium transition-all duration-300 ${
          currentFaction === "grok"
            ? "bg-grok text-grok-foreground hover:bg-grok/80 transform scale-105"
            : "text-muted-foreground hover:text-foreground"
        }`}
        data-testid="button-faction-grok"
      >
        <Brain className={`mr-1 h-3 w-3 transition-transform duration-300 ${
          factionMutation.isPending ? "animate-pulse" : ""
        }`} />
        Grok
      </Button>
      <Button
        variant={currentFaction === "ani" ? "default" : "ghost"}
        size="sm"
        onClick={() => handleFactionChange("ani")}
        disabled={factionMutation.isPending}
        className={`px-3 py-1 text-sm font-medium transition-all duration-300 ${
          currentFaction === "ani"
            ? "bg-ani text-ani-foreground hover:bg-ani/80 transform scale-105"
            : "text-muted-foreground hover:text-foreground"
        }`}
        data-testid="button-faction-ani"
      >
        <Heart className={`mr-1 h-3 w-3 transition-transform duration-300 ${
          factionMutation.isPending ? "animate-pulse" : ""
        }`} />
        Ani
      </Button>
    </div>
  );
}

client/src/components/leaderboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Brain, Heart, Trophy, Medal, Award, Clock, Star, Coins, Gem } from "lucide-react";
import { useState } from "react";
import type { User } from "@shared/schema";

interface LeaderboardProps {
  faction?: "grok" | "ani";
  category?: "points" | "xp" | "gac" | "nfts" | "voting";
  period?: "daily" | "weekly" | "monthly" | "all-time";
  limit?: number;
  showControls?: boolean;
}

export function Leaderboard({ 
  faction = "grok", 
  category = "points", 
  period = "all-time", 
  limit = 10,
  showControls = false 
}: LeaderboardProps) {
  const [selectedCategory, setSelectedCategory] = useState(category);
  const [selectedPeriod, setSelectedPeriod] = useState(period);

  const { data: leaderboard = [], isLoading } = useQuery<User[]>({
    queryKey: [`/api/leaderboard/${faction}`, selectedCategory, selectedPeriod, limit],
    refetchInterval: 30000, // Auto-refresh every 30 seconds
  });

  const factionConfig = {
    grok: {
      icon: Brain,
      color: "text-grok",
      bgColor: "faction-grok",
      title: "Grok's Analysts",
      pointsLabel: "GrokPoints"
    },
    ani: {
      icon: Heart,
      color: "text-ani", 
      bgColor: "faction-ani",
      title: "Ani's Creatives",
      pointsLabel: "AniPoints"
    }
  };

  const config = factionConfig[faction];
  const Icon = config.icon;

  const getRankIcon = (position: number) => {
    if (position === 1) return <Trophy className="h-4 w-4 text-yellow-500" />;
    if (position === 2) return <Medal className="h-4 w-4 text-gray-400" />;
    if (position === 3) return <Award className="h-4 w-4 text-amber-600" />;
    return <span className="text-sm font-bold">{position}</span>;
  };

  return (
    <Card className={`${config.bgColor} border-2`} data-testid={`leaderboard-${faction}`}>
      <CardHeader>
        <CardTitle className={`${config.color} flex items-center gap-2`}>
          <Icon className="h-5 w-5" />
          {config.title}
        </CardTitle>
      </CardHeader>
      <CardContent>
        {isLoading ? (
          <div className="flex items-center justify-center p-8">
            <div className={`animate-spin rounded-full h-8 w-8 border-b-2 border-${faction}`}></div>
          </div>
        ) : leaderboard.length > 0 ? (
          <div className="space-y-3">
            {leaderboard.slice(0, Math.min(limit, 5)).map((user, index: number) => (
              <div 
                key={user.id} 
                className="flex items-center justify-between bg-card rounded-lg p-3"
                data-testid={`leaderboard-item-${faction}-${index}`}
              >
                <div className="flex items-center space-x-3">
                  <div className="w-8 h-8 rounded-full bg-muted flex items-center justify-center">
                    {getRankIcon(index + 1)}
                  </div>
                  <div className="flex items-center space-x-2">
                    <Avatar className="h-6 w-6">
                      <AvatarImage src={user.profileImageUrl} />
                      <AvatarFallback className="text-xs">
                        {(user.firstName?.[0] || user.email?.[0] || "U").toUpperCase()}
                      </AvatarFallback>
                    </Avatar>
                    <div>
                      <div className="font-medium text-sm" data-testid={`text-username-${index}`}>
                        {user.firstName || user.email?.split('@')[0] || 'Anonymous'}
                      </div>
                      <div className="text-xs text-muted-foreground">
                        Level {user.level || 1}
                      </div>
                    </div>
                  </div>
                </div>
                <div className={`${config.color} font-bold text-sm`} data-testid={`text-points-${index}`}>
                  {faction === "grok" ? user.grokPoints || 0 : user.aniPoints || 0}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center text-muted-foreground p-8">
            <Icon className="h-12 w-12 mx-auto mb-2 opacity-50" />
            <p>No rankings yet. Be the first to earn {config.pointsLabel}!</p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

