                  <SelectValue placeholder="Select a faction" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="grok">ðŸ”µ Grok (Logic & Analysis)</SelectItem>
                  <SelectItem value="ani">ðŸŸ£ Ani (Emotion & Creativity)</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="pin">Create 4-Digit PIN</Label>
              <Input 
                id="pin"
