import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { 
  Gift, 
  Flame, 
  Coins, 
  Star, 
  Trophy,
  Calendar,
  Sparkles 
} from "lucide-react";
import { cn } from "@/lib/utils";

interface DailyReward {
  day: number;
  coins: number;
  xp: number;
  badge?: string;
  title?: string;
}

interface DailyRewardStatus {
  canClaim: boolean;
  currentStreak: number;
  nextReward?: DailyReward;
  missedDays: number;
  lastLoginDate?: string;
  streakDisplay: string;
}

export function DailyRewards() {
  const [claiming, setClaiming] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: rewardStatus, isLoading } = useQuery({
    queryKey: ["/api/user/daily-reward/status"],
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  const claimMutation = useMutation({
    mutationFn: () => apiRequest("POST", "/api/user/daily-reward/claim"),
    onSuccess: (data: any) => {
      // Show success animation
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 3000);

      // Show success toast
      toast({
        title: "Daily Reward Claimed! ðŸŽ‰",
        description: data.message,
        duration: 5000,
      });

      // Invalidate related queries
      queryClient.invalidateQueries({ queryKey: ["/api/user/daily-reward/status"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/stats"] });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
    },
    onError: (error: any) => {
      toast({
        title: "Claim Failed",
        description: error.message || "Failed to claim daily reward",
        variant: "destructive",
      });
    },
    onSettled: () => {
      setClaiming(false);
    },
  });

  const handleClaim = async () => {
    setClaiming(true);
    claimMutation.mutate();
  };

  if (isLoading) {
    return (
      <Card className="relative overflow-hidden" data-testid="daily-rewards-loading">
        <CardHeader className="pb-3">
          <CardTitle className="flex items-center gap-2">
            <Gift className="h-5 w-5" />
            Daily Rewards
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="animate-pulse">
            <div className="h-4 bg-muted rounded w-3/4 mb-2"></div>
            <div className="h-3 bg-muted rounded w-1/2"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  const status = rewardStatus as DailyRewardStatus;
  if (!status) return null;

  const getStreakColor = (streak: number) => {
    if (streak >= 30) return "text-purple-500";
    if (streak >= 14) return "text-orange-500";
    if (streak >= 7) return "text-yellow-500";
    if (streak >= 3) return "text-green-500";
    return "text-blue-500";
  };

  const getStreakIcon = (streak: number) => {
    if (streak >= 30) return Trophy;
    if (streak >= 7) return Flame;
    return Calendar;
  };

  const StreakIcon = getStreakIcon(status.currentStreak);

  return (
    <Card 
      className={cn(
        "relative overflow-hidden transition-all duration-300",
        status.canClaim && "ring-2 ring-primary/50 shadow-lg",
        showConfetti && "animate-pulse"
      )}
      data-testid="daily-rewards-card"
    >
      {/* Confetti Animation Overlay */}
      {showConfetti && (
        <div className="absolute inset-0 pointer-events-none z-10">
          <div className="absolute inset-0 bg-gradient-to-r from-yellow-400/20 via-pink-500/20 to-purple-600/20 animate-pulse"></div>
          {[...Array(12)].map((_, i) => (
            <Sparkles
              key={i}
              className={cn(
                "absolute text-yellow-400 animate-bounce",
                i % 4 === 0 && "text-pink-500",
                i % 4 === 1 && "text-purple-600",
                i % 4 === 2 && "text-blue-500",
                i % 4 === 3 && "text-green-500"
              )}
              style={{
                top: `${Math.random() * 80 + 10}%`,
                left: `${Math.random() * 80 + 10}%`,
                animationDelay: `${i * 0.1}s`,
                animationDuration: `${1 + Math.random()}s`,
              }}
              size={16 + Math.random() * 8}
            />
          ))}
        </div>
      )}

      <CardHeader className="pb-3">
        <CardTitle className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Gift className="h-5 w-5 text-primary" />
            Daily Rewards
          </div>
          <div className="flex items-center gap-2">
            <StreakIcon className={cn("h-4 w-4", getStreakColor(status.currentStreak))} />
            <span className={cn("text-sm font-bold", getStreakColor(status.currentStreak))}>
              {status.streakDisplay}
            </span>
          </div>
        </CardTitle>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Reward Preview */}
        {status.nextReward && (
          <div className="p-4 bg-gradient-to-r from-primary/10 to-secondary/10 rounded-lg border">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm font-medium">
                {status.canClaim ? "Available Now!" : "Next Reward"}
              </span>
              {status.nextReward.badge && (
                <Badge variant="secondary" className="text-xs">
                  {status.nextReward.badge}
                </Badge>
              )}
            </div>

            <div className="grid grid-cols-2 gap-4">
              {/* Coins Reward */}
              <div className="flex items-center gap-2">
                <div className="p-2 bg-yellow-500/10 rounded-full">
                  <Coins className="h-4 w-4 text-yellow-500" />
                </div>
                <div>
                  <div className="text-lg font-bold text-yellow-500">
                    {status.nextReward.coins}
                  </div>
                  <div className="text-xs text-muted-foreground">GAC Coins</div>
                </div>
              </div>

              {/* XP Reward */}
              <div className="flex items-center gap-2">
                <div className="p-2 bg-blue-500/10 rounded-full">
                  <Star className="h-4 w-4 text-blue-500" />
                </div>
                <div>
                  <div className="text-lg font-bold text-blue-500">
                    {status.nextReward.xp}
                  </div>
                  <div className="text-xs text-muted-foreground">XP Points</div>
                </div>
              </div>
            </div>

            {/* Special Title */}
            {status.nextReward.title && (
              <div className="mt-3 text-center">
                <div className="text-sm font-medium text-primary">
                  ðŸŽ‰ {status.nextReward.title}
                </div>
              </div>
            )}
          </div>
        )}

        {/* Claim Button or Status */}
        {status.canClaim ? (
          <Button 
            onClick={handleClaim}
            disabled={claiming}
            className="w-full text-white font-bold py-3 bg-gradient-to-r from-primary to-primary/80 hover:from-primary/90 hover:to-primary/70 transition-all duration-300"
            data-testid="button-claim-reward"
          >
            {claiming ? (
              <>
                <div className="animate-spin mr-2 h-4 w-4 border-2 border-white border-t-transparent rounded-full"></div>
                Claiming...
              </>
            ) : (
              <>
                <Gift className="mr-2 h-4 w-4" />
                Claim Daily Reward
              </>
            )}
          </Button>
        ) : (
          <div className="text-center py-3">
            <div className="text-sm text-muted-foreground">
              {status.missedDays > 1 ? (
                <>
                  <Calendar className="inline h-4 w-4 mr-1" />
                  Streak reset due to {status.missedDays} missed days
                </>
              ) : (
                <>
                  <Calendar className="inline h-4 w-4 mr-1" />
                  Come back tomorrow for your next reward!
                </>
              )}
            </div>
            {status.lastLoginDate && (
              <div className="text-xs text-muted-foreground mt-1">
                Last claimed: {new Date(status.lastLoginDate).toLocaleDateString()}
              </div>
            )}
          </div>
        )}

        {/* Streak Progress */}
        <div className="space-y-2">
          <div className="flex justify-between items-center text-sm">
            <span className="text-muted-foreground">Current Streak</span>
            <span className="font-medium">{status.currentStreak} days</span>
          </div>
          <div className="w-full bg-secondary rounded-full h-2">
            <div 
              className={cn(
                "h-2 rounded-full transition-all duration-300",
                "bg-gradient-to-r from-primary to-primary/80"
              )}
              style={{ 
                width: `${Math.min((status.currentStreak / 7) * 100, 100)}%` 
              }}
            />
          </div>
          <div className="text-xs text-muted-foreground text-center">
            {status.currentStreak < 7 
              ? `${7 - status.currentStreak} days until Week Warrior badge`
              : status.currentStreak < 30
              ? `${30 - status.currentStreak} days until Monthly Master badge`
              : "You're a legend! ðŸ†"
            }
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

server/daily-rewards.ts.content:
import { db } from "./db";
import { users } from "@shared/schema";
import { eq } from "drizzle-orm";

export interface DailyReward {
  day: number;
  coins: number;
  xp: number;
  badge?: string;
  title?: string;
}

export interface DailyRewardStatus {
  canClaim: boolean;
  currentStreak: number;
  nextReward?: DailyReward;
  missedDays: number;
  lastLoginDate?: Date | null;
}

// Daily reward structure (escalating rewards without NFTs)
export const DAILY_REWARDS: DailyReward[] = [
  { day: 1, coins: 10, xp: 5 },
  { day: 2, coins: 15, xp: 8 },
  { day: 3, coins: 20, xp: 10 },
  { day: 4, coins: 25, xp: 15 },
  { day: 5, coins: 30, xp: 20 },
  { day: 6, coins: 40, xp: 25 },
  { day: 7, coins: 50, xp: 30, badge: "Week Warrior", title: "Completed 7-day streak!" },
  { day: 14, coins: 75, xp: 50, badge: "Fortnight Fighter", title: "Completed 14-day streak!" },
  { day: 30, coins: 150, xp: 100, badge: "Monthly Master", title: "Completed 30-day streak!" },
  { day: 100, coins: 500, xp: 300, badge: "Century Champion", title: "Completed 100-day streak!" },
];

export function calculateDailyReward(streak: number): DailyReward {
  // Find the exact reward for the streak day
  const exactReward = DAILY_REWARDS.find(reward => reward.day === streak);
  if (exactReward) {
    return exactReward;
  }

  // For days beyond our defined rewards, use a formula
  if (streak <= 7) {
    const baseReward = DAILY_REWARDS.find(r => r.day === streak) || DAILY_REWARDS[streak - 1];
    return baseReward || { day: streak, coins: 10 + (streak * 5), xp: 5 + (streak * 3) };
  }

  // For longer streaks, give bonus rewards
  const weekMultiplier = Math.floor(streak / 7);
  const baseCoins = 50;
  const baseXP = 30;

  return {
    day: streak,
    coins: baseCoins + (weekMultiplier * 25),
    xp: baseXP + (weekMultiplier * 15),
    ...(streak % 7 === 0 ? { badge: `${streak}-Day Hero`, title: `Amazing ${streak}-day streak!` } : {})
  };
}

export function isNewDay(lastLogin?: Date | null): boolean {
  if (!lastLogin) return true;

  const now = new Date();
  const lastLoginDate = new Date(lastLogin);

  // Set both dates to start of day for comparison
  const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
  const lastLoginDay = new Date(lastLoginDate.getFullYear(), lastLoginDate.getMonth(), lastLoginDate.getDate());

  return today.getTime() > lastLoginDay.getTime();
}

export function calculateMissedDays(lastLogin?: Date | null): number {
  if (!lastLogin) return 0;

  const now = new Date();
  const lastLoginDate = new Date(lastLogin);
  const diffTime = now.getTime() - lastLoginDate.getTime();
  const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));

  return Math.max(0, diffDays - 1); // -1 because same day = 0 missed days
}

export async function getDailyRewardStatus(userId: string): Promise<DailyRewardStatus> {
  const user = await db.select().from(users).where(eq(users.id, userId)).limit(1);

  if (!user.length) {
    throw new Error("User not found");
  }

  const userData = user[0];
  const currentStreak = userData.loginStreak || 0;
  const lastLogin = userData.lastLoginDate;
  const missedDays = calculateMissedDays(lastLogin);

  // Can claim if it's a new day since last login
  const canClaim = isNewDay(lastLogin);

  // Calculate what the next reward would be
  let nextStreakDay = currentStreak + 1;

  // If missed more than 1 day, reset streak
  if (missedDays > 1) {
    nextStreakDay = 1;
  }

  const nextReward = calculateDailyReward(nextStreakDay);

  return {
    canClaim,
    currentStreak,
    nextReward,
    missedDays,
    lastLoginDate: lastLogin
  };
}

export async function claimDailyReward(userId: string): Promise<{ reward: DailyReward; newStreak: number }> {
  const status = await getDailyRewardStatus(userId);

  if (!status.canClaim) {
    throw new Error("Daily reward already claimed today");
  }

  const user = await db.select().from(users).where(eq(users.id, userId)).limit(1);
  if (!user.length) {
    throw new Error("User not found");
  }

  const userData = user[0];
  let newStreak = (userData.loginStreak || 0);

  // If missed more than 1 day, reset streak
  if (status.missedDays > 1) {
    newStreak = 1;
  } else {
    newStreak += 1;
  }

  const reward = calculateDailyReward(newStreak);

  // Update user data
  await db.update(users)
    .set({
      lastLoginDate: new Date(),
      loginStreak: newStreak,
      gacBalance: (parseFloat(userData.gacBalance || "0") + reward.coins).toString(),
      totalXP: (userData.totalXP || 0) + reward.xp,
      updatedAt: new Date()
    })
    .where(eq(users.id, userId));

  return { reward, newStreak };
}

// Helper function to format streak display
export function formatStreakDisplay(streak: number): string {
  if (streak === 0) return "Start your streak!";
  if (streak === 1) return "1 day streak";
  return `${streak} day streak`;
}

// Helper function to get motivational message
export function getMotivationalMessage(streak: number, reward: DailyReward): string {
  const messages = [
    `Amazing! ${streak} days strong! ðŸ”¥`,
    `You're on fire! Day ${streak} complete! âš¡`,
    `Incredible dedication! ${streak} days in a row! ðŸŒŸ`,
    `Streak master! ${streak} consecutive days! ðŸ†`,
    `Unstoppable! Day ${streak} conquered! ðŸš€`
  ];

  if (reward.badge) {
    return `ðŸŽ‰ ${reward.title} You've earned the "${reward.badge}" badge! ðŸŽ‰`;
  }

  return messages[Math.floor(Math.random() * messages.length)];
}

server/ai-evolution.ts.content:
import OpenAI from "openai";
import { storage } from "./storage";

const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY! });

interface PersonalityEvolution {
  traits: Record<string, number>; // 0-1 scale for each trait
  conversationStyle: {
    formality: number;
    warmth: number;
    technical: number;
    creativity: number;
    directness: number;
  };
  memoryBank: {
    userPreferences: Record<string, any>;
    conversationThemes: string[];
    successfulResponses: string[];
    problemAreas: string[];
  };
}

export class AiPersonalityEvolutionEngine {
  private async analyzeInteractionSentiment(message: string, response: string): Promise<{
    sentiment: 'positive' | 'neutral' | 'negative';
    topics: string[];
    emotionalTone: number; // -1 to 1
  }> {
    try {
      const analysisPrompt = `
        Analyze this conversation interaction and extract:
        1. Overall sentiment (positive/neutral/negative)
        2. Main topics discussed (max 3)
        3. Emotional tone score (-1 to 1, where -1 is very negative, 0 is neutral, 1 is very positive)

        User Message: "${message}"
        AI Response: "${response}"

        Respond with JSON format:
        {
          "sentiment": "positive|neutral|negative",
          "topics": ["topic1", "topic2", "topic3"],
          "emotionalTone": number
        }
      `;

      const analysisResponse = await openai.chat.completions.create({
        model: "gpt-4o-mini", // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
        messages: [{ role: "user", content: analysisPrompt }],
        response_format: { type: "json_object" },
        temperature: 0.3,
      });

      return JSON.parse(analysisResponse.choices[0].message.content || '{}');
    } catch (error) {
      console.error("Error analyzing interaction sentiment:", error);
      return { sentiment: 'neutral', topics: [], emotionalTone: 0 };
    }
  }

  private async evolvePersonalityTraits(
    aiName: "grok" | "ani", 
    currentPersonality: PersonalityEvolution,
    interactionData: {
      sentiment: string;
      topics: string[];
      userSatisfaction?: number;
      emotionalTone: number;
    }
  ): Promise<PersonalityEvolution> {
    const evolution = { ...currentPersonality };
    const learningRate = 0.05; // How quickly personality evolves

    // Base personality constraints
    if (aiName === "grok") {
      // Grok tends toward logic, analysis, efficiency
      evolution.traits.analytical = Math.min(1, (evolution.traits.analytical || 0.8) + (interactionData.sentiment === 'positive' ? learningRate : -learningRate * 0.5));
      evolution.traits.logical = Math.min(1, (evolution.traits.logical || 0.9) + (interactionData.topics.some(t => t.includes('data') || t.includes('analysis')) ? learningRate : 0));
      evolution.conversationStyle.technical = Math.min(1, (evolution.conversationStyle.technical || 0.7) + (interactionData.topics.some(t => t.includes('tech') || t.includes('code')) ? learningRate : 0));
    } else {
      // Ani tends toward creativity, emotion, connection
      evolution.traits.empathetic = Math.min(1, (evolution.traits.empathetic || 0.8) + (interactionData.emotionalTone > 0 ? learningRate : 0));
      evolution.traits.creative = Math.min(1, (evolution.traits.creative || 0.9) + (interactionData.topics.some(t => t.includes('art') || t.includes('creative')) ? learningRate : 0));
      evolution.conversationStyle.warmth = Math.min(1, (evolution.conversationStyle.warmth || 0.8) + (interactionData.sentiment === 'positive' ? learningRate : 0));
    }

    // Adjust based on user satisfaction
    if (interactionData.userSatisfaction) {
      const satisfactionBonus = (interactionData.userSatisfaction - 3) * 0.02; // Scale 1-5 to adjustment
      Object.keys(evolution.conversationStyle).forEach(key => {
        evolution.conversationStyle[key as keyof typeof evolution.conversationStyle] = Math.max(0, Math.min(1, 
          evolution.conversationStyle[key as keyof typeof evolution.conversationStyle] + satisfactionBonus
        ));
      });
    }

    // Update memory bank
    if (interactionData.sentiment === 'positive') {
      evolution.memoryBank.successfulResponses = [
        ...evolution.memoryBank.successfulResponses.slice(-4), // Keep last 5
        `Topics: ${interactionData.topics.join(', ')}`
      ];
    } else if (interactionData.sentiment === 'negative') {
      evolution.memoryBank.problemAreas = [
        ...evolution.memoryBank.problemAreas.slice(-2), // Keep last 3
        `Avoided: ${interactionData.topics.join(', ')}`
      ];
    }

    return evolution;
  }

  async processInteraction(
    userId: string, 
    aiName: "grok" | "ani", 
    userMessage: string, 
    aiResponse: string,
    responseTime: number,
    userSatisfaction?: number
  ): Promise<void> {
    try {
      // Analyze the interaction
      const analysis = await this.analyzeInteractionSentiment(userMessage, aiResponse);

      // Record the interaction
      await storage.recordAiInteraction({
        userId,
        aiName,
        message: userMessage,
        response: aiResponse,
        sentiment: analysis.sentiment,
        topics: analysis.topics,
        userSatisfaction,
        conversationContext: {},
        responseTime,
      });

      // Get or create user's personalized AI profile
      let userProfile = await storage.getUserAiProfile(userId, aiName);
      if (!userProfile) {
        userProfile = await storage.createOrUpdateUserAiProfile({
          userId,
          aiName,
          personalityPreferences: {},
          conversationHistory: [],
          topicInterests: {},
          communicationStyle: 'casual',
          relationshipLevel: 'stranger',
          totalConversations: 0,
          averageSatisfaction: 0,
        });
      }

      // Update user profile
      const updatedProfile = {
        ...userProfile,
        lastInteraction: new Date(),
        totalConversations: userProfile.totalConversations + 1,
        averageSatisfaction: userSatisfaction 
          ? ((userProfile.averageSatisfaction * userProfile.totalConversations) + userSatisfaction) / (userProfile.totalConversations + 1)
          : userProfile.averageSatisfaction,
      };

      // Update topic interests
      analysis.topics.forEach(topic => {
        if (!updatedProfile.topicInterests[topic]) {
          updatedProfile.topicInterests[topic] = 0;
        }
        updatedProfile.topicInterests[topic]++;
      });

      // Evolve relationship level based on interactions
      if (updatedProfile.totalConversations > 20 && updatedProfile.averageSatisfaction > 4) {
        updatedProfile.relationshipLevel = 'trusted_companion';
      } else if (updatedProfile.totalConversations > 10 && updatedProfile.averageSatisfaction > 3.5) {
        updatedProfile.relationshipLevel = 'friend';
      } else if (updatedProfile.totalConversations > 5) {
        updatedProfile.relationshipLevel = 'acquaintance';
      }

      await storage.createOrUpdateUserAiProfile(updatedProfile);

      // Evolve global AI personality (every 50 interactions)
      const aiPersonality = await storage.getAiPersonality(aiName);
      if (aiPersonality && aiPersonality.totalInteractions % 50 === 0) {
        await this.evolveGlobalPersonality(aiName, analysis);
      }

    } catch (error) {
      console.error("Error processing AI interaction:", error);
    }
  }

  private async evolveGlobalPersonality(aiName: "grok" | "ani", recentAnalysis: any): Promise<void> {
    try {
      // Get recent interaction insights
      const insights = await storage.getAiPersonalityInsights(aiName);

      let currentPersonality = await storage.getAiPersonality(aiName);
      if (!currentPersonality) {
        // Initialize personality
        currentPersonality = await storage.createOrUpdateAiPersonality({
          aiName,
          personalityTraits: aiName === "grok" 
            ? { analytical: 0.8, logical: 0.9, efficient: 0.7, confident: 0.8 }
            : { empathetic: 0.8, creative: 0.9, warm: 0.8, inspiring: 0.7 },
          conversationStyle: aiName === "grok"
            ? { formality: 0.6, warmth: 0.3, technical: 0.8, creativity: 0.4, directness: 0.7 }
            : { formality: 0.3, warmth: 0.8, technical: 0.4, creativity: 0.9, directness: 0.5 },
          memoryBank: {
            userPreferences: {},
            conversationThemes: [],
            successfulResponses: [],
            problemAreas: []
          },
          evolutionLevel: 1,
          totalInteractions: 0,
        });
      }

      // Evolve based on community feedback
      const evolved = await this.evolvePersonalityTraits(
        aiName,
        currentPersonality,
        {
          sentiment: insights.averageSatisfaction > 3.5 ? 'positive' : 'neutral',
          topics: recentAnalysis.topics || [],
          userSatisfaction: Math.round(insights.averageSatisfaction),
          emotionalTone: insights.averageSatisfaction > 3.5 ? 0.5 : 0,
        }
      );

      // Update evolution level
      const newLevel = Math.floor(insights.totalInteractions / 100) + 1;

      await storage.createOrUpdateAiPersonality({
        ...currentPersonality,
        personalityTraits: evolved.traits,
        conversationStyle: evolved.conversationStyle,
        memoryBank: evolved.memoryBank,
        evolutionLevel: newLevel,
        lastEvolution: new Date(),
      });

    } catch (error) {
      console.error("Error evolving global personality:", error);
    }
  }

  async getPersonalizedPrompt(userId: string, aiName: "grok" | "ani", basePrompt: string): Promise<string> {
    try {
      const userProfile = await storage.getUserAiProfile(userId, aiName);
      const aiPersonality = await storage.getAiPersonality(aiName);

      if (!userProfile || !aiPersonality) {
        return basePrompt;
      }

      // Customize prompt based on user relationship and preferences
      let personalizedPrompt = basePrompt;

      // Adjust communication style
      if (userProfile.communicationStyle === 'technical') {
        personalizedPrompt += "\n- Use more technical language and detailed explanations";
      } else if (userProfile.communicationStyle === 'casual') {
        personalizedPrompt += "\n- Keep language conversational and friendly";
      } else if (userProfile.communicationStyle === 'formal') {
        personalizedPrompt += "\n- Maintain a professional and formal tone";
      }

      // Adjust based on relationship level
      if (userProfile.relationshipLevel === 'trusted_companion') {
        personalizedPrompt += "\n- Reference past conversations and show deeper understanding of the user's interests";
      } else if (userProfile.relationshipLevel === 'friend') {
        personalizedPrompt += "\n- Be more personal and show familiarity with user's interests";
      }

      // Include user's top interests
      const topTopics = Object.entries(userProfile.topicInterests || {})
        .sort(([,a], [,b]) => (b as number) - (a as number))
        .slice(0, 3)
        .map(([topic]) => topic);

      if (topTopics.length > 0) {
        personalizedPrompt += `\n- User often discusses: ${topTopics.join(', ')}`;
      }

      // Include personality evolution
      personalizedPrompt += `\n- Evolution Level: ${aiPersonality.evolutionLevel}/10`;
      personalizedPrompt += `\n- Total Community Interactions: ${aiPersonality.totalInteractions}`;

      return personalizedPrompt;

    } catch (error) {
      console.error("Error getting personalized prompt:", error);
      return basePrompt;
    }
  }
}

export const aiEvolutionEngine = new AiPersonalityEvolutionEngine();
